SELECT id, username, email, password, bio, image, created_at, updated_at
FROM users 
WHERE email = #{email}

SELECT id, username, email, password, bio, image, created_at, updated_at
FROM users 
WHERE username = #{username}

SELECT id, username, email, password, bio, image, created_at, updated_at
FROM users 
WHERE id = #{id}

SELECT COUNT(*) > 0 
FROM users 
WHERE email = #{email}

SELECT COUNT(*) > 0 
FROM users 
WHERE username = #{username}
